package com.example.car_app;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.ArrayList;public class AdapterDetail extends RecyclerView.Adapter<AdapterDetail.ViewHolder> {    private ArrayList<CarDetail> carListDetail;    public AdapterDetail(ArrayList<CarDetail> carListDetail) {        this.carListDetail = carListDetail;    }    @NonNull    @Override    public AdapterDetail.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view= LayoutInflater.from(parent.getContext()).inflate(R.layout.item_details,parent,false);        return new ViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull AdapterDetail.ViewHolder holder, int position) {        holder.txtNumDetail.setText(carListDetail.get(position).getCarNumDetail()+"");        holder.txtDescDetail.setText(carListDetail.get(position).getCarDescDetail());    }    @Override    public int getItemCount() {        return carListDetail.size();    }    public class ViewHolder extends RecyclerView.ViewHolder {        private TextView txtDescDetail,txtNumDetail;        public ViewHolder(@NonNull View itemView) {            super(itemView);            txtDescDetail=itemView.findViewById(R.id.txtDescDetail);            txtNumDetail=itemView.findViewById(R.id.txtNumDetail);        }    }}