package com.example.car_app;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.app.VoiceInteractor;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.CheckBox;import android.widget.CompoundButton;import android.widget.ImageButton;import android.widget.RelativeLayout;import android.widget.TextView;import android.widget.Toast;public class DetailsActivity extends AppCompatActivity implements CompoundButton.OnCheckedChangeListener {    private TextView txtTitleDetail, txtVinDetail, txtStockDetail;    private RecyclerView carDetailRecView;    private AdapterDetail adapterDetail ;    private CheckBox cB_NoIssue, cB_Issue, cB_NA;    private RelativeLayout parent1;    private ImageButton btnBack;    Utils utils = new Utils();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_details);        parent1 = findViewById(R.id.parent1);        carDetailRecView=findViewById(R.id.carDetailRecView);        txtTitleDetail=findViewById(R.id.txtTitleDetail);        txtVinDetail=findViewById(R.id.txtVinDetail);        txtStockDetail=findViewById(R.id.txtStockDetail);        cB_NoIssue = findViewById(R.id.cB_NoIssue);        cB_Issue = findViewById(R.id.cB_Issue);        cB_NA = findViewById(R.id.cB_NA);        btnBack = findViewById(R.id.btnBack);        btnBack.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent = new Intent(DetailsActivity.this,MainActivity.class);                startActivity(intent);            }        });        utils.initCarDetails();        txtTitleDetail.setText(getIntent().getExtras().getString("title"));        txtVinDetail.setText(getIntent().getExtras().getString("vin"));        txtStockDetail.setText(getIntent().getExtras().getString("stock"));        adapterDetail = new AdapterDetail(utils.getCarDetails());        carDetailRecView.setAdapter(adapterDetail);        carDetailRecView.setLayoutManager(new LinearLayoutManager(this));    }    @Override    public void onCheckedChanged(CompoundButton buttonView, boolean isChecked) {    }    @Override    public void onBackPressed() {        super.onBackPressed();        Intent intent= new Intent(this,MainActivity.class);        intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TASK | Intent.FLAG_ACTIVITY_NEW_TASK);        startActivity(intent);    }}