package com.example.car_app;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.os.Bundle;import android.widget.TextView;public class DetailsActivity extends AppCompatActivity {    private TextView txtTitleDetail, txtVinDetail, txtStockDetail;    private RecyclerView carDetailRecView;    private AdapterDetail adapterDetail ;    Utils utils = new Utils();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_details);        carDetailRecView=findViewById(R.id.carDetailRecView);        txtTitleDetail=findViewById(R.id.txtTitleDetail);        txtVinDetail=findViewById(R.id.txtVinDetail);        txtStockDetail=findViewById(R.id.txtStockDetail);        utils.initCarDetails();        txtTitleDetail.setText(getIntent().getExtras().getString("title"));        txtVinDetail.setText(getIntent().getExtras().getString("vin"));        txtStockDetail.setText(getIntent().getExtras().getString("stock"));        adapterDetail = new AdapterDetail(utils.getCarDetails());        carDetailRecView.setAdapter(adapterDetail);        carDetailRecView.setLayoutManager(new LinearLayoutManager(this));    }}