package com.example.car_app;import android.content.Context;import android.content.Intent;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CheckBox;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.bumptech.glide.Glide;import java.util.ArrayList;import static android.content.Intent.FLAG_ACTIVITY_NEW_TASK;public class MyAdapter extends RecyclerView.Adapter<MyAdapter.MyViewHolder> {    private ArrayList<Car> carsList;    public MyAdapter(ArrayList<Car> carsList){        this.carsList=carsList;    }    public void changeList(ArrayList<Car> carsPassed) {        this.carsList = carsPassed;    }    public class MyViewHolder extends RecyclerView.ViewHolder{        private TextView txtTitle, txtStock, txtVIN, txtColor, txtMileage,txtDate;        private CheckBox cBpassedFailed,cBFailed;        private ImageView imgCar;        private RelativeLayout relativeLayout;        private TextView txtTitleD, txtStockD, txtVIND,txtViewInspectionDetail;        public MyViewHolder(final View view){            super(view);            txtTitle = view.findViewById(R.id.txtTitle);            txtStock = view.findViewById(R.id.txtStock);            txtVIN = view.findViewById(R.id.txtVIN);            txtColor = view.findViewById(R.id.txtColor);            txtMileage = view.findViewById(R.id.txtMileage);            txtDate = view.findViewById(R.id.txtDate);            cBpassedFailed = view.findViewById(R.id.checkBoxPassedDetail);            cBFailed = view.findViewById(R.id.checkBoxFailedDetail);            imgCar = view.findViewById(R.id.imageCar);            relativeLayout=view.findViewById(R.id.relative);            txtTitleD = itemView.findViewById(R.id.bottomTitleDetail);            txtStockD = itemView.findViewById(R.id.stockDetail);            txtVIND = itemView.findViewById(R.id.vinDetail);            txtViewInspectionDetail = itemView.findViewById(R.id.txtViewInspection);        }    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view= LayoutInflater.from(parent.getContext()).inflate(R.layout.list_item,parent,false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position) {        final Car temp=carsList.get(position);        holder.txtTitle.setText(carsList.get(position).getTitle());        holder.txtStock.setText(carsList.get(position).getStock());        holder.txtVIN.setText(carsList.get(position).getVin());        holder.txtColor.setText(carsList.get(position).getColor());        holder.txtMileage.setText(carsList.get(position).getMileage());        holder.txtDate.setText(carsList.get(position).getDate());        Glide.with(holder.itemView.getContext())                .load(carsList.get(position).getImgUrl())                .into(holder.imgCar);        if(carsList.get(position).isPassedFailed()!=true) {            holder.cBpassedFailed.setVisibility(View.GONE);            holder.cBFailed.setVisibility(View.VISIBLE);        }else{            holder.cBpassedFailed.setVisibility(View.VISIBLE);            holder.cBFailed.setVisibility(View.GONE);        }        holder.relativeLayout.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent= new Intent(holder.itemView.getContext(),SpecificDetailActivity.class);                intent.putExtra("title",temp.getTitle());                intent.putExtra("vin",temp.getVin());                intent.putExtra("stock",temp.getStock());                intent.putExtra("color",temp.getColor());                intent.putExtra("mileage",temp.getMileage());                intent.putExtra("date",temp.getDate());                intent.putExtra("cBPassedFailed",temp.isPassedFailed());                intent.putExtra("imgCar",temp.getImgUrl());                intent.setFlags(FLAG_ACTIVITY_NEW_TASK);                holder.itemView.getContext().startActivity(intent);            }        });    }    @Override    public int getItemCount() {        return carsList.size();    }    public void filterList(ArrayList<Car> filterList){        carsList=filterList;        notifyDataSetChanged();    }}