package com.example.car_app;import androidx.appcompat.app.AppCompatActivity;import androidx.cardview.widget.CardView;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.CheckBox;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import static android.content.Intent.FLAG_ACTIVITY_NEW_TASK;public class SpecificDetailActivity extends AppCompatActivity {    private ImageButton imgBtnBackDetail;    private TextView txtTopTitleDetail, txtBottomTitleDetail, txtStockDetail, txtVinDetail, txtColorDetail, txtMileageDetail,txtDateDetail,txtViewInspection;    private CheckBox cBPassedDetail, cBFailedDetail;    private ImageView imgDetail;    private CardView cardSubmitRepaireOrder, cardViewInspection, cardPrintInspection, cardViewFactfile, cardPrintFactfile;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_specific_detail);        find_Id();        setData();        imgBtnBackDetail.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {               onBackPressed();            }        });        txtViewInspection.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {               Intent intent = new Intent(SpecificDetailActivity.this,DetailsActivity.class);               intent.putExtra("title",getIntent().getStringExtra("title"));               intent.putExtra("vin",getIntent().getStringExtra("vin"));               intent.putExtra("stock",getIntent().getStringExtra("stock"));               intent.setFlags(FLAG_ACTIVITY_NEW_TASK);                startActivity(intent);            }        });    }    @Override    public void onBackPressed(){        super.onBackPressed();        Intent intent = new Intent(this,MainActivity.class);        intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TASK | Intent.FLAG_ACTIVITY_NEW_TASK);        startActivity(intent);    }    public void setData(){        txtTopTitleDetail.setText(getIntent().getStringExtra("title"));        txtBottomTitleDetail.setText(getIntent().getStringExtra("title"));        txtStockDetail.setText(getIntent().getStringExtra("stock"));        txtVinDetail.setText(getIntent().getStringExtra("vin"));        txtColorDetail.setText(getIntent().getStringExtra("color"));        txtMileageDetail.setText(getIntent().getStringExtra("mileage"));        txtDateDetail.setText(getIntent().getStringExtra("date"));        if(getIntent().getBooleanExtra("cBPassedFailed",true)){            cBFailedDetail.setVisibility(View.GONE);            cardSubmitRepaireOrder.setVisibility(View.GONE);        }else{            cBPassedDetail.setVisibility(View.GONE);            cardViewFactfile.setVisibility(View.GONE);            cardPrintFactfile.setVisibility(View.GONE);        }        Glide.with(this).asBitmap().load(getIntent().getStringExtra("imgCar")).into(imgDetail);    }    private void find_Id(){        imgBtnBackDetail = findViewById(R.id.btnBackDetail);        txtTopTitleDetail = findViewById(R.id.topTitleDetail);        txtBottomTitleDetail = findViewById(R.id.bottomTitleDetail);        txtStockDetail = findViewById(R.id.stockDetail);        txtVinDetail = findViewById(R.id.vinDetail);        txtColorDetail = findViewById(R.id.colorDetail);        txtMileageDetail = findViewById(R.id.mileageDetail);        txtDateDetail = findViewById(R.id.dateDetail);        txtViewInspection = findViewById(R.id.txtViewInspection);        cBPassedDetail = findViewById(R.id.checkBoxPassedDetail);        cBFailedDetail = findViewById(R.id.checkBoxFailedDetail);        imgDetail = findViewById(R.id.imgDetail);        cardSubmitRepaireOrder = findViewById(R.id.cardSubmitRepaireOrder);        cardViewInspection = findViewById(R.id.cardViewInspection);        cardPrintInspection = findViewById(R.id.cardPrintInspection);        cardViewFactfile = findViewById(R.id.cardViewFactfile);        cardPrintFactfile = findViewById(R.id.cardPrintFactfile);    }}